cy.task('checkUserExists', testUser.email).then(function (exists) {
    if (!exists) {
        throw new Error("L'utilisateur de test ".concat(testUser.email, " n'existe pas apr\u00E8s le seeding."));
    }
    cy.log("V\u00E9rification : L'utilisateur ".concat(testUser.email, " existe."));
    // Se connecter
    cy.loginByApi(testUser.email, testUser.password);
    // Vérifier que nous sommes bien authentifiés - juste une vérification minimale
    cy.request({
        method: 'GET',
        url: '/api/auth/me',
        failOnStatusCode: false
    }).then(function (response) {
        expect(response.status).to.eq(200);
        expect(response.body).to.have.property('authenticated', true);
        expect(response.body.user).to.have.property('role');
        cy.log("Utilisateur authentifi\u00E9 avec le r\u00F4le: ".concat(response.body.user.role));
    });
});

