var keyboardNavigation = {
    currentIndex: 0,
    focusableElements: [],
    navigationPath: []
};
// Identifier tous les √©l√©ments focusables
cy.get('body').then(function ($body) {
    var focusableSelectors = [
        'input:not([disabled]):not([type="hidden"])',
        'button:not([disabled])',
        'select:not([disabled])',
        'textarea:not([disabled])',
        'a[href]',
        '[tabindex]:not([tabindex="-1"])',
        '[contenteditable]'
    ].join(', ');
    var focusableElements = $body.find(focusableSelectors).toArray();
    keyboardNavigation.focusableElements = focusableElements;
    cy.log("Found ".concat(focusableElements.length, " focusable elements"));
});
// Test de navigation TAB compl√®te
cy.get('body').focus();
// Parcourir tous les √©l√©ments avec TAB
for (var i = 0; i < 20; i++) { // Limiter pour les tests
    cy.get('body').tab();
    cy.focused().then(function ($focused) {
        if ($focused.length > 0) {
            var elementInfo = {
                tagName: $focused[0].tagName,
                type: $focused[0].type,
                id: $focused[0].id,
                'data-cy': $focused[0].getAttribute('data-cy'),
                'aria-label': $focused[0].getAttribute('aria-label'),
                visible: $focused.is(':visible')
            };
            keyboardNavigation.navigationPath.push(elementInfo);
            // V√©rifier que l'√©l√©ment est visible et a un focus visible
            cy.wrap($focused)
                .should('be.visible')
                .and('satisfy', function ($el) {
                var styles = window.getComputedStyle($el[0]);
                var hasOutline = styles.outline !== 'none' && styles.outline !== '';
                var hasBoxShadow = styles.boxShadow !== 'none';
                var hasCustomFocus = $el.hasClass('focus-visible') || $el.hasClass('focused');
                return hasOutline || hasBoxShadow || hasCustomFocus;
            });
        }
    });
    cy.wait(100); // Pause pour stabilit√©
}
