cy.visit('/admin/dashboard');
// Mesurer différentes métriques
cy.window().then(function (win) {
    var performance = win.performance;
    // Temps de chargement initial
    var navigationStart = performance.timing.navigationStart;
    var domContentLoaded = performance.timing.domContentLoadedEventEnd;
    var loadComplete = performance.timing.loadEventEnd;
    var domTime = domContentLoaded - navigationStart;
    var loadTime = loadComplete - navigationStart;
    cy.log("DOM Ready: ".concat(domTime, "ms"));
    cy.log("Load Complete: ".concat(loadTime, "ms"));
    expect(domTime).to.be.lessThan(1500);
    expect(loadTime).to.be.lessThan(3000);
    // Enregistrer dans un fichier pour suivi
    cy.writeFile('cypress/reports/performance-metrics.json', {
        timestamp: new Date().toISOString(),
        domTime: domTime,
        loadTime: loadTime,
        page: '/admin/dashboard'
    }, { flag: 'a+' });
});
